    private void c1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_c1MouseClicked
        // TODO add your handling code here:
        if (c1.isSelected()){
            bname.setText(StudentInformation.name.getText());
        }
        else
            bname.setText(null);
    }//GEN-LAST:event_c1MouseClicked

    private void c2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_c2MouseClicked
        // TODO add your handling code here:
        if (c2.isSelected()){
            bid.setText(StudentInformation.id.getText());
        }
        else
            bid.setText(null);
    }//GEN-LAST:event_c2MouseClicked

    private void c3MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_c3MouseClicked
        // TODO add your handling code here:
        if (c3.isSelected()){
            bdept.setText(StudentInformation.dept.getText());
        }
        else
            bdept.setText(null);
    }//GEN-LAST:event_c3MouseClicked

    private void c4MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_c4MouseClicked
        // TODO add your handling code here:
        if (c4.isSelected()){
            bissue.setText(BorrowDateC.BdateC.getText());
        }
        else
            bissue.setText(null);
    }//GEN-LAST:event_c4MouseClicked

    private void c5MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_c5MouseClicked

        if (c5.isSelected()){
            bbook.append("\n\tBorrow BOOK LIST\n"+
                    "\n\t" + BookListCommerceB.bfin.getText()+
                    "\n\t" +BookListCommerceB.bmar.getText() +
                    "\n\t" +BookListCommerceB.bbmath.getText() +
                    "\n\t" +BookListCommerceB.bcomp.getText() +
                    "\n\n" );
        }
        else
            bbook.setText(null);
    }//GEN-LAST:event_c5MouseClicked

